import React, { useState } from 'react';
import Project from '../components/project/Project';
import { projects } from './../helpers/projectsList';
import { Pagination } from 'react-bootstrap';

const Projects = () => {
    const itemsPerPage = 6; // Number of projects per page
    const [currentPage, setCurrentPage] = useState(1);

    // Calculate the index range for the current page
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    // Slice the projects array to get the projects for the current page
    const currentProjects = projects.slice(startIndex, endIndex);

    // Calculate the total number of pages
    const totalPages = Math.ceil(projects.length / itemsPerPage);

    // Function to handle page changes
    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    return (
        <main className="section">
            <div className="container">
                <h2 className="title-1">Projects</h2>

                {projects.length === 0 ? (
                    <p>No projects available.</p>
                ) : (
                    <div>
                        <ul className="projects">
                            {currentProjects.map((project, index) => (
                                <Project key={index} title={project.title} img={project.img} index={index} />
                            ))}
                        </ul>

                        {/* Render react-bootstrap pagination controls */}
                        <div className="text-center mt-4">
                            <Pagination>
                                <Pagination.Prev
                                    onClick={() => handlePageChange(currentPage - 1)}
                                    disabled={currentPage === 1}
                                />
                                {Array.from({ length: totalPages }).map((_, index) => (
                                    <Pagination.Item
                                        key={index + 1}
                                        active={index + 1 === currentPage}
                                        onClick={() => handlePageChange(index + 1)}
                                    >
                                        {index + 1}
                                    </Pagination.Item>
                                ))}
                                <Pagination.Next
                                    onClick={() => handlePageChange(currentPage + 1)}
                                    disabled={currentPage === totalPages}
                                />
                            </Pagination>
                        </div>
                    </div>
                )}
            </div>
        </main>
    );
};

export default Projects;
